function init(){canvas=document.getElementById("canvas"),ctx=canvas.getContext("2d"),timer=setInterval(draw,10),ctx.canvas.width=window.innerWidth,ctx.canvas.height=window.innerHeight}function draw(){ctx.clearRect(0,0,canvas.width,canvas.height),ctx.drawImage(square,posX,posY,squareWidth,squareHeight)}var canvas,ctx,timer,posX=0,posY=0,square=new Image;square.src="square.png";var squareWidth=20,squareHeight=20;window.addEventListener("keydown",function(e){switch(e.keyCode){case 38:posY-=10;break;case 40:posY+=10;break;case 37:posX-=10;break;case 39:posX+=10;break}});